function assemble_boundary_mass_operator(points, tris, C)
  #preallocate sparse matrix description
  ii=Array{UInt32}(undef,6^2,length(tris))
  jj=Array{UInt32}(undef,6^2,length(tris))
  mm=Array{ComplexF64}(undef,6^2,length(tris))
    for (idx,(smplx,c)) in enumerate(zip(tris,C))
        tri=points[:,smplx[1:3]]
        m=Array{ComplexF64}(undef,6,6)
        c1,c2,c3=c#C[smplx[1:3]]
        m[1,1]=c1/84 + c2/420 + c3/420
        m[1,2]=-c1/630 - c2/630 + c3/2520
        m[1,3]=-c1/630 + c2/2520 - c3/630
        m[1,4]=c1/210 - c2/315 - c3/630
        m[1,5]=c1/210 - c2/630 - c3/315
        m[1,6]=-c1/630 - c2/210 - c3/210
        m[2,2]=c1/420 + c2/84 + c3/420
        m[2,3]=c1/2520 - c2/630 - c3/630
        m[2,4]=-c1/315 + c2/210 - c3/630
        m[2,5]=-c1/210 - c2/630 - c3/210
        m[2,6]=-c1/630 + c2/210 - c3/315
        m[3,3]=c1/420 + c2/420 + c3/84
        m[3,4]=-c1/210 - c2/210 - c3/630
        m[3,5]=-c1/315 - c2/630 + c3/210
        m[3,6]=-c1/630 - c2/315 + c3/210
        m[4,4]=4*c1/105 + 4*c2/105 + 4*c3/315
        m[4,5]=2*c1/105 + 4*c2/315 + 4*c3/315
        m[4,6]=4*c1/315 + 2*c2/105 + 4*c3/315
        m[5,5]=4*c1/105 + 4*c2/315 + 4*c3/105
        m[5,6]=4*c1/315 + 4*c2/315 + 2*c3/105
        m[6,6]=4*c1/315 + 4*c2/105 + 4*c3/105
        m[2,1]=m[1,2]
        m[3,1]=m[1,3]
        m[3,2]=m[2,3]
        m[4,1]=m[1,4]
        m[4,2]=m[2,4]
        m[4,3]=m[3,4]
        m[5,1]=m[1,5]
        m[5,2]=m[2,5]
        m[5,3]=m[3,5]
        m[5,4]=m[4,5]
        m[6,1]=m[1,6]
        m[6,2]=m[2,6]
        m[6,3]=m[3,6]
        m[6,4]=m[4,6]
        m[6,5]=m[5,6]
        #compute triangle surface i.e. determinant of the local coordinate transform
        A=LinearAlgebra.norm(LinearAlgebra.cross(tri[:,1]-tri[:,3],tri[:,2]-tri[:,3]))#/2
        m=m*A
        j=repeat(smplx,1,6)#
        i=j'
        ii[:,idx]=i[:]
        jj[:,idx]=j[:]
        mm[:,idx]=m[:]
    end
    return ii[:], jj[:], mm[:]
end


function assemble_volume_source(points,tets)
  #preallocate sparse matrix description
  ii=Array{UInt32}(undef,10,length(tets))
  mm=Array{ComplexF64}(undef,10,length(tets))
  m_unit=[-1/120  -1/120  -1/120  -1/120  1/30  1/30  1/30  1/30  1/30  1/30]

  for (idx,smplx) in enumerate(tets)
    tet=points[:,smplx[1:4]]

    #local coordinate transformation
    J=tet[:,1:3]-tet[:,[4,4,4]]
    #mass_matrix in global coordinates
    m=m_unit*abs(LinearAlgebra.det(J))
    ii[:,idx]=smplx[:]
    mm[:,idx]=m[:]
  end

  return ii[:], mm[:]
end

function assemble_gradient_source(points,smplx, x_ref, n_ref)
    tet=points[:,smplx[1:4]]
    ii=smplx
    #local coordinate transformation
    J=tet[:,1:3]-tet[:,[4,4,4]]
    J_inv=inv(J)
    x,y,z=J_inv*(x_ref-points[:,smplx[4]])
    grad=[4*x-1 0 0 ;
          0 4*y-1 0 ;
          0 0 4*z-1 ;
          4*x+4*y+4*z-3 4*x+4*y+4*z-3 4*x+4*y+4*z-3 ;
          4*y 4*x 0 ;
          4*z 0 4*x ;
          -8*x-4*y-4*z+4 -4*x -4*x ;
          0 4*z 4*y ;
          -4*y -4*x-8*y-4*z+4 -4*y ;
          -4*z -4*z -4*x-4*y-8*z+4 ]
    mm=grad*J_inv*n_ref
    return ii[:], mm[:]
end
